
public class ex_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5; // int: 정수를 저장하는 Java의 기본 데이터 유형 (int x; + x =5;를 합친것)
		System.out.println(x);
		
		x = 10;
		System.out.println(x); // 기존에 저장되어 있던 5는 지워지고 10이 출력됨.
		
		int a = 10;
		int b = 5;
		
		System.out.println(a+b); 
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		
	}

}
